 function porcentagem($parcial, $total) {
    if (!$total) {
     return '0.0';
    }
    $return = ( $parcial * 100 ) / $total;
    return number_format($return, 1, '.', '');
   }
 
   function formatData($data) {
    return substr($data, 8, 2) . '/' . substr($data, 5, 2) . '/' . substr($data, 0, 4);
   }

 public function getDataDashboard($dados) {

  $periodo = [];
  $dados['mensuracao']=array_filter($dados['mensuracao']);
  if(!empty($dados['mensuracao'])){
    $firstKey = array_key_first($dados['mensuracao']);
    $periodo['inicio']=$dados['mensuracao'][$firstKey]['inicio'];
    $periodo['fim']=$dados['mensuracao'][count($dados['mensuracao'])]['fim'];
  }

  $codPesquisa = $dados['pesquisa'];
  $pesquisa = $this->repoPesquisa->where('id',$codPesquisa)->get();
  $pesquisa = $pesquisa[0];
  $tPesquisaEnt=substr($pesquisa['id_token'], 0, 5);

  $filtros = $dados['filtros'];
  $totalEntrevistados = 0;
  $net_scope = '';
  // $respRelacionada = array('SEMI_ABERTA', 'FECHADA_RESPOSTA_UNICA', 'ESCALA_LIKERT', 'ESCALA_ITEMIZADA', 'ESCALA_INTENCAO_COMPRA');
//   $respRelacionada = array('FECHADA_RESPOSTA_UNICA');

  // $dicotomica = ['MASC_FEMIN' => ['Masculino', 'Feminino'], 'SIM_NAO' => ['SIM', 'NO'], 'VERD_FALSO' => ['Verdadeiro', 'Falso']];

  ///////// filtros
  $sqlWhere = [];
  $sqlJoin = [];
  $sqlOn = [];
  $numFil = 2;
 
  if (!empty($filtros)) {
   foreach ($filtros as $filtro) {
    if (!empty($filtro['value']) && $filtro['value'] != null) {
     $aliasTb = $numFil++;
     $aliasTb = "t{$aliasTb}";
     $sqlJoin[] = " api_respostas AS {$aliasTb} ";
     $sqlOn[] = " t1.id={$aliasTb}.id_internauta ";
     if ($filtro['tipo_pergunta'] == 'FECHADA_RESPOSTA_MULTIPLA') {
      $fAux = [];

      foreach ($filtro['value'] as $fMult) {
       if ($fMult) {
        $fAux[] = " ({$aliasTb}.id_resposta LIKE '{$fMult}' "
                . " OR {$aliasTb}.id_resposta LIKE '{$fMult},%' "
                . " OR {$aliasTb}.id_resposta LIKE '%,{$fMult},%'"
                . " OR {$aliasTb}.id_resposta LIKE '%,{$fMult}' )";
       }
      }
      if (count($fAux)) {
       $whereFil = " (" . join(' OR ', $fAux) . ")";
      }
     } elseif ($filtro['tipo_pergunta'] == 'NET_PROMOTER_SCORE') {
      $fAuxNps = [];
      foreach ($filtro['value'] as $fNps) {
       $fAuxNps[] = " ({$aliasTb}.id_resposta='{$fNps}') ";
      }
      $whereFil = '(' . join(' OR ', $fAuxNps) . ')';
     } elseif ($filtro['tipo_pergunta'] == 'AVALIACAO_SATISFACAO') {
      $fAuxAS = [];
      foreach ($filtro['value'] as $fAS) {
       $fAuxAS[] = " ( {$aliasTb}.id_resposta='" . $fAS . "' )";
      }
      $whereFil = '(' . join(' OR ', $fAuxAS) . ')';
     } elseif ($filtro['tipo_pergunta'] == 'MONETARIO'||$filtro['tipo_pergunta'] == 'NUMERICO'||$filtro['tipo_pergunta'] == 'DATA') {
      $fAuxD = [];
      $valAux= explode('_',$filtro['value']);
      if(!empty($valAux[0])){
        $fAuxD[] = " ( {$aliasTb}.resposta>='{$valAux[0]}')";
      }
      if(!empty($valAux[1])){
        $fAuxD[] = " ( {$aliasTb}.resposta<='{$valAux[1]}')";
      }
       
      $whereFil = '(' . join(' AND ', $fAuxD) . ')';
     } else {
      $fAuxOu = [];
      foreach ($filtro['value'] as $fD) {
       $fAuxOu[] = " ({$aliasTb}.id_resposta='" . $fD . "' )";
      }
      $whereFil = '(' . join(' OR ', $fAuxOu) . ')';
     }
     $sqlWhere[] = " ({$aliasTb}.status='SIM' AND {$aliasTb}.id_pergunta={$filtro['id_pergunta']} AND  $whereFil) ";
    }
   }
  }
  // dd($sqlWhere);
  $sqlJoin = (count($sqlJoin)) ? join(' JOIN ', $sqlJoin) : ' api_respostas AS t2 ';
  $sqlOn = (count($sqlOn)) ? join(' AND ', $sqlOn) : ' t1.id=t2.id_internauta ';
  $sqlFilter = (count($sqlWhere)) ? '(' . join(' AND ', $sqlWhere) . ')' : " t2.status='SIM' ";

  $wherePeriodo = '';
 
  if (!empty($periodo['inicio']) && !empty($periodo['fim'])) {
   $wherePeriodo = " AND  SUBSTRING(t1.data_cadastro, 1, 10) >= '{$periodo['inicio']}' AND SUBSTRING(t1.data_cadastro, 1, 10) <= '{$periodo['fim']}' ";
  }

  $sql = "SELECT t1.* FROM api_respostas_internauta AS t1 JOIN {$sqlJoin}"
          . " ON {$sqlOn} "
          . " WHERE t1.status='SIM' AND t1.id_pesquisa={$codPesquisa} AND {$sqlFilter} {$wherePeriodo} GROUP BY t1.id ORDER BY t1.data_cadastro";

  $interResp = DB::select($sql, []);

  $totalEntrevistados = count($interResp);
  $buscPerguntas = $this->repositorie_pergunta->where('status','SIM')->where('id_pesquisa',$codPesquisa)->orderBy('ordem','ASC')->get();

  $return = [];
  if(count($dados['mensuracao'])>1){
    $return = $this->__dataDashboardMensuracao($dados['mensuracao'],$interResp,$buscPerguntas,$tPesquisaEnt);
 
}else{
    $return = $this->__dataDashboard($interResp,$buscPerguntas,$tPesquisaEnt);
    $return['totalEntrevistados'] = $totalEntrevistados;
  }
//   dd($return);
  return $return;

}

