            $retornRelatorio[$indice]['justificativa'] = $justificativa;
            $retornRelatorio[$indice]['net_scope'] = $net_scope;
            }
            $retornRelatorio[$indice]['data'] = $data;
            $retornRelatorio[$indice]['fases'] = $fases;
            $retornRelatorio[$indice]['label'] = $label;
            $indice+=1;
        }
        }
        //// tratamento da fase
        $retFase = [];
        foreach ($dFases as $valFase) {
        $retFase[] = $valFase;
        }


        return ['status' => true, 'fases' => $retFase, 'dados' => $retornRelatorio];
}

