 function getFitros($user,$data){
     $return=['mensuracao'=>[],'indicadores'=>[1=>0,2=>0,3=>0,4=>0],'fPerguntas'=>[],'filtros'=>[]];
     $filtros =$this->repoPesquisaDashboard->where('id_usuario',$user['id'])->where('id_pesquisa',$data['pesquisa'])->get();
     if(count($filtros)){
         $filtros=$filtros[0];
        for($i=1;$i<=3;$i++){
          if($filtros["m".$i."_data_inicio"]&&$filtros["m".$i."_data_fim"]){
            $return['mensuracao'][$i]=['inicio'=>$filtros["m".$i."_data_inicio"],'fim'=>$filtros["m".$i."_data_fim"]];
          }

          if($filtros['indicador'.$i]){
              $return['indicadores'][$i]=$filtros['indicador'.$i];  
          }
        }
              if($filtros['indicador4']){
                  $return['indicadores'][4]=$filtros['indicador4'];  
              }
        if($filtros['json_filtros_perguntas']){
            foreach(json_decode($filtros['json_filtros_perguntas']) as $key => $value) {
                $return['filtros'][$key]=(array)$value;
            }
        }
     }
     return $return;
 }
 function alterarFitros($user,$data){
    $campos=[];
    $filtros=[];
    foreach ($data['filtros'] as $val) {
        if($val){
            $filtros[$val['id_pergunta']]=['id_pergunta'=>$val['id_pergunta'],'tipo_pergunta'=>$val['tipo_pergunta'],'value'=>$val['value']];
        }
    }
    $campos['json_filtros_perguntas']=json_encode($filtros);
    for($i=1;$i<=3;$i++){
        $campos["m".($i)."_data_inicio"]=(!empty($data['mensuracao'][$i]['inicio']))?$data['mensuracao'][$i]['inicio']:'';
        $campos["m".($i)."_data_fim"]=(!empty($data['mensuracao'][$i]['fim']))?$data['mensuracao'][$i]['fim']:'';
    }
    $ckeck = $this->repoPesquisaDashboard->where('id_usuario',$user['id'])->where('id_pesquisa',$data['pesquisa']);
    if(count($ckeck->get())){
        $ckeck->update($campos);
    }else{
        $campos['id_usuario']=$user['id'];
        $campos['id_pesquisa']=$data['pesquisa'];
        $campos['status']='SIM';
        $ckeck->create($campos);
    }
 }
