 function alterarIndicadores($user,$data){
    $ckeck = $this->repoPesquisaDashboard->where('id_usuario',$user['id'])->where('id_pesquisa',$data['pesquisa']);
    $campos=[ 'indicador'.$data['num_indicador']=>$data['id']];
    if(count($ckeck->get())){
        $ckeck->update($campos);
    }else{
        $campos['id_usuario']=$user['id'];
        $campos['id_pesquisa']=$data['pesquisa'];
        $campos['status']='SIM';
        $ckeck->create($campos);
    }
    return ['status'=>true];
 }

